'use strict';

/**
* Test if a numeric value is infinite.
*
* @module @stdlib/utils/is-infinite
*
* @example
* var isInfinite = require( '@stdlib/math/base/utils/is-infinite' );
*
* var v = isInfinite( Number.POSITIVE_INFINITY );
* // returns true
*
* v = isInfinite( Number.NEGATIVE_INFINITY );
* // returns true
*
* v = isInfinite( 5.0 );
* // returns false
*
* v = isInfinite( NaN );
* // returns false
*/

// MODULES //

var isInfinite = require( './is_infinite.js' );


// EXPORTS //

module.exports = isInfinite;
