'use strict';

/**
* Tests if a numeric value is infinite.
*
* @module @stdlib/utils/is-infinite
*
* @example
* var isInfinite = require( '@stdlib/math/base/utils/is-infinite' );
*
* var v = isInfinite( +Infinity );
* // returns true
*
* v = isInfinite( -Infinity );
* // returns true
*
* v = isInfinite( 5 );
* // returns false
*
* v = isInfinite( NaN );
* // returns false
*/

// MODULES //

var isInfinite = require( './isinfinite.js' );


// EXPORTS //

module.exports = isInfinite;
