'use strict';

// ISNAN //

/**
* FUNCTION: isnan( x )
*	Tests if a numeric value is `NaN`.
*
* @param {Number} x - value to be tested
* @returns {Boolean} boolean indicating whether the value is `NaN`
*/
function isnan( x ) {
	return (x !== x);
} // end FUNCTION isnan()


// EXPORTS //

module.exports = isnan;
