'use strict';

// MODULES //

var NINF = require( '@stdlib/math/base/constants/float64-ninf' );


// IS NEGATIVE ZERO //

/**
* FUNCTION: isNegativeZero( x )
*	Tests if a numeric value is `-0`.
*
* @param {Number} x - value to be tested
* @returns {Boolean} boolean indicating whether the value is `-0`
*/
function isNegativeZero( x ) {
	return (x === 0 && 1/x === NINF);
} // end FUNCTION isNegativeZero()


// EXPORTS //

module.exports = isNegativeZero;
