'use strict';

// MODULES //

var floor = require( '@stdlib/math/base/special/floor' );


// IS NONPOSITIVE INTEGER //

/**
* FUNCTION: isNonPositiveInteger( x )
*	Tests if a finite double-precision floating-point number is a non-positive integer.
*
* @param {Number} x - value to be tested
* @returns {Boolean} boolean indicating whether the value is a non-positive integer
*/
function isNonPositiveInteger( x ) {
	return (floor(x) === x && x <= 0);
} // end FUNCTION isNonPositiveInteger()


// EXPORTS //

module.exports = isNonPositiveInteger;
