'use strict';

var isPositiveInteger = require( './../lib' );

console.log( isPositiveInteger( 5.0 ) );
// returns true

console.log( isPositiveInteger( 0.0 ) );
// returns false

console.log( isPositiveInteger( -1.0 ) );
// returns false

console.log( isPositiveInteger( 3.14 ) );
// returns false

console.log( isPositiveInteger( NaN ) );
// returns false
