'use strict';

// MODULES //

var tape = require( 'tape' );
var uint32ToInt32 = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof uint32ToInt32, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function converts unsigned 32-bit integers to signed 32-bit integers', function test( t ) {
	var expected;
	var x;
	var y;
	var i;

	x = data.x;
	expected = data.expected;
	for ( i = 0; i < x.length; i++ ) {
		y = uint32ToInt32( x[ i ] );
		t.equal( y, expected[ i ], 'y: '+y+', expected: '+expected[ i ] );
	}
	t.end();
});
