'use strict';

/**
* Difference between one and the smallest value greater than one that can be represented as a half-precision floating-point number.
*
* @module @stdlib/math/constants/float16-eps
* @type {number}
*
* @example
* var FLOAT16_EPSILON = require( '@stdlib/math/constants/float16-eps' );
* // returns 0.0009765625
*/

// MAIN //

/**
* Difference between one and the smallest value greater than one that can be represented as a half-precision floating-point number is
*
* ``` tex
* \frac{1}{2^{10}}
* ```
*
* @constant
* @type {number}
* @default 0.0009765625
* @see [Half-precision floating-point format]{@link https://en.wikipedia.org/wiki/Half-precision_floating-point_format}
* @see [Machine Epsilon]{@link https://en.wikipedia.org/wiki/Machine_epsilon}
*/
var FLOAT16_EPSILON = 0.0009765625; // In JavaScript, can only represent as a double-precision floating-point number (float64)


// EXPORTS //

module.exports = FLOAT16_EPSILON;
