'use strict';

/**
* The bias of a half-precision floating-point number's exponent.
*
* @module @stdlib/math/constants/float16-exponent-bias
* @type {integer32}
*
* @example
* var FLOAT16_EXPONENT_BIAS = require( '@stdlib/math/constants/float16-exponent-bias' );
* // returns 15
*/


// MAIN //

/**
* The bias of a half-precision floating-point number's exponent. The bias can be computed via
*
* ``` tex
* \mathrm{bias} = 2^{k-1} - 1
* ```
*
* where \\(k\\) is the number of bits in the exponent; here, \\(k = 5\\).
*
* @constant
* @type {integer32}
* @default 15
* @see [Half-precision floating-point format]{@link https://en.wikipedia.org/wiki/Half-precision_floating-point_format}
*/
var FLOAT16_EXPONENT_BIAS = 15|0; // asm type annotation


// EXPORTS //

module.exports = FLOAT16_EXPONENT_BIAS;
