'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var FLOAT16_MIN_SAFE_INTEGER = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.equal( typeof FLOAT16_MIN_SAFE_INTEGER, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is -2**11+1', function test( t ) {
	t.equal( FLOAT16_MIN_SAFE_INTEGER, -pow( 2.0, 11 )+1, 'returns -2**11+1' );
	t.end();
});
