'use strict';

// MODULES //

var tape = require( 'tape' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var FLOAT16_EPSILON = require( '@stdlib/math/constants/float16-eps' );
var FLOAT16_SQRT_EPSILON = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof FLOAT16_SQRT_EPSILON, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals the square root of half-precision floating-point epsilon', function test( t ) {
	var expected = sqrt( FLOAT16_EPSILON );
	t.strictEqual( FLOAT16_SQRT_EPSILON, expected, 'equals sqrt(2**-10)' );
	t.end();
});
