'use strict';

/**
* Smallest positive single-precision floating-point subnormal number.
*
* @module @stdlib/math/constants/float32-smallest-subnormal
* @type {Number}
*
* @example
* var FLOAT32_SMALLEST_SUBNORMAL = require( '@stdlib/math/constants/float32-smallest-subnormal' );
* // returns 1.401298464324817e-45
*/


// CONSTANT //

/**
* The smallest positive single-precision floating-point subnormal number is
*
* ``` equation
* \frac{1}{2^{127-1} 2^{23}}
* ```
*
* @constant
* @type {Number}
* @default 1.401298464324817e-45
*/

var FLOAT32_SMALLEST_SUBNORMAL = 1.401298464324817e-45;


// EXPORTS //

module.exports = FLOAT32_SMALLEST_SUBNORMAL;

