'use strict';

var FLOAT64_EXPONENT_MASK = require( './../lib' );
var toBinaryString = require( '@stdlib/math/base/utils/uint32-to-binary-string' );
var getHighWord = require( '@stdlib/math/base/utils/float64-get-high-word' );

var out;
var hi;
var x;

x = 11.5;
hi = getHighWord( x );
console.log( 'Word: %d', toBinaryString( hi ) );

out = hi & FLOAT64_EXPONENT_MASK;
console.log( 'Isolate exponent bits: %d', toBinaryString( out ) );

out = hi | FLOAT64_EXPONENT_MASK;
console.log( 'Turn on exponent bits: %d', toBinaryString( out ) );
