'use strict';

// MODULES //

var tape = require( 'tape' );
var FLOAT64_GAMMA_LANCZOS_G = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof FLOAT64_GAMMA_LANCZOS_G, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals `10.900511`', function test( t ) {
	var expected = 10.900511;
	t.strictEqual( FLOAT64_GAMMA_LANCZOS_G, expected, 'equals 10.900511' );
	t.end();
});
