'use strict';

// MODULES //

var tape = require( 'tape' );
var ln = require( '@stdlib/math/base/special/ln' );
var abs = require( '@stdlib/math/base/special/abs' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var HALF_LN2 = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof HALF_LN2, 'number', 'main export is a number' );
	t.end();
});

tape( 'export is a double-precision floating-point number equal to `3.46573590279972654709e-01`', function test( t ) {
	t.equal( HALF_LN2, 3.46573590279972654709e-01, 'equals 3.46573590279972654709e-01' );
	t.end();
});

tape( 'export equals `0.5*ln(2)`', function test( t ) {
	var delta;
	var tol;
	var v;

	v = 0.5 * ln( 2.0 );
	delta = abs( v - HALF_LN2 );
	tol = EPS * HALF_LN2;

	t.ok( delta <= tol, 'equals 0.5*ln(2) within tolerance '+tol );

	t.end();
});
