'use strict';

// MODULES //

var tape = require( 'tape' );
var LNPI = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof LNPI, 'number', 'main export is a number' );
	t.end();
});

tape( 'export is a double-precision floating-point number equal to 1.1447298858494002', function test( t ) {
	t.equal( LNPI, 1.1447298858494002, 'equals 1.1447298858494002' );
	t.end();
});
