'use strict';

/**
* The maximum biased binary exponent for a subnormal double-precision floating-point number.
*
* @module @stdlib/math/constants/float64-max-binary-exponent-subnormal
* @type {integer32}
*
* @example
* var FLOAT64_MAX_BINARY_EXPONENT_SUBNORMAL = require( '@stdlib/math/constants/float64-max-binary-exponent-subnormal' );
* // returns -1023
*/


// CONSTANT //

/**
* The maximum biased binary exponent for a subnormal double-precision floating-point number.
*
* ``` text
* 00000000000 => 0 - BIAS = -1023
* ```
* where the `BIAS = 1023`.
*
* @constant
* @type {integer32}
* @default -1023
* @see [IEEE 754]{@link https://en.wikipedia.org/wiki/IEEE_754-1985}
*/
var FLOAT64_MAX_BINARY_EXPONENT_SUBNORMAL = -1023|0; // asm type annotation


// EXPORTS //

module.exports = FLOAT64_MAX_BINARY_EXPONENT_SUBNORMAL;
