'use strict';

// MODULES //

var tape = require( 'tape' );
var FLOAT64_MAX_SAFE_FIBONACCI = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof FLOAT64_MAX_SAFE_FIBONACCI, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is 8944394323791464', function test( t ) {
	t.strictEqual( FLOAT64_MAX_SAFE_FIBONACCI, 8944394323791464, 'returns expected value' );
	t.end();
});
