'use strict';

// MODULES //

var tape = require( 'tape' );
var FLOAT64_MIN_BASE10_EXPONENT_NORMAL = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof FLOAT64_MIN_BASE10_EXPONENT_NORMAL, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is `-308`', function test( t ) {
	t.equal( FLOAT64_MIN_BASE10_EXPONENT_NORMAL, -308, 'equals -308' );
	t.end();
});
