'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var INT16_MIN = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof INT16_MIN, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals -2**15', function test( t ) {
	t.equal( INT16_MIN, -pow(2,15), 'equals -1 * 2**15' );
	t.end();
});
