'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var INT8_MAX = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof INT8_MAX, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals 2**7 - 1', function test( t ) {
	t.equal( INT8_MAX, pow(2,7)-1, 'equals 2**7-1' );
	t.end();
});
