'use strict';

/**
* Size (in bytes) of a 32-bit unsigned integer.
*
* @module @stdlib/math/constants/uint32-num-bytes
* @type {integer32}
*
* @example
* var UINT32_NUM_BYTES = require( '@stdlib/math/constants/uint32-num-bytes' );
* // returns 4
*/


// MAIN //

/**
* Size (in bytes) of a 32-bit unsigned integer.
*
* @constant
* @type {integer32}
* @default 4
*/
var UINT32_NUM_BYTES = 4|0; // asm type annotation


// EXPORTS //

module.exports = UINT32_NUM_BYTES;
