'use strict';

/**
* Size (in bytes) of an 8-bit unsigned integer.
*
* @module @stdlib/math/constants/uint8-num-bytes
* @type {integer32}
*
* @example
* var UINT8_NUM_BYTES = require( '@stdlib/math/constants/uint8-num-bytes' );
* // returns 1
*/


// MAIN //

/**
* Size (in bytes) of an 8-bit unsigned integer.
*
* @constant
* @type {integer32}
* @default 1
*/
var UINT8_NUM_BYTES = 1|0; // asm type annotation


// EXPORTS //

module.exports = UINT8_NUM_BYTES;
