'use strict';

// VARIABLES //

var b = new Uint32Array( [ 0x2, 0xC, 0xF0, 0xFF00, 0xFFFF0000 ] );
var S = new Uint32Array( [ 1, 2, 4, 8, 16 ] );


// MAIN //

/**
* Computes an integer binary logarithm (base two).
*
* @param {integer32} x - input value
* @returns {integer32} integer binary logarithm
*
* @example
* var v = llog2( 4 );
* // returns 2
* @example
* var v = llog2( 8 );
* // returns 3
* @example
* var v = llog2( 9 );
* // returns 3
*/
function llog2( x ) {
	var out = 0;
	var i;

	for ( i = 4; i >= 0; i-- ) {
		if ( x & b[ i ] ) {
			x >>= S[ i ];
			out |= S[ i ];
		}
	}
	return out;
} // end FUNCTION llog2()


// EXPORTS //

module.exports = llog2;
