'use strict';

// MODULES //

var tape = require( 'tape' );
var minstd = require( '@stdlib/math/base/random/minstd-shuffle' );
var log2 = require( '@stdlib/math/base/special/log2' );
var floor = require( '@stdlib/math/base/special/floor' );
var llog2 = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof llog2, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an approximate binary logarithm (base two) for integer arguments', function test( t ) {
	var x;
	var y;
	var v;
	var i;

	for ( i = 0; i < 5000; i++ ) {
		x = minstd();
		y = log2( x );
		v = llog2( x );
		t.strictEqual( v, floor( y ), 'returns an approximate binary logarithm' );
	}
	t.end();
});
