'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );
var randu = require( '@stdlib/math/base/random/randu' );
var floor = require( '@stdlib/math/base/special/floor' );
var pow = require( '@stdlib/math/base/special/pow' );
var fastpow = require( './../lib' );


// VARIABLES //

var N = 1e7;


// FUNCTIONS //

function warmup() {
	var i;
	var j;

	j = 0;
	for ( i = 0; i < 1e6; i++ ) {
		j += i * 2;
	}
	if ( j > 0 ) {
		return true;
	}
	return false;
}

function test( name, fcn ) {
	var start;
	var time;
	var bool;
	var x;
	var y;
	var h;
	var i;

	bool = warmup();
	if ( !bool ) {
		throw new Error( 'Something went wrong.' );
	}
	start = process.hrtime();
	for ( i = 0; i < N; i++ ) {
		x = randu()*100.0 - 50.0;
		y = floor( randu()*100.0 ) - 50;
		h = fcn( x, y );
		if ( isnan( h ) ) {
			throw new Error( 'Something went wrong.' );
		}
	}
	time = process.hrtime( start );
	time = time[ 0 ] + time[ 1 ]*1.0e-9;

	if ( isnan( h ) ) {
		throw new Error( 'Something went wrong.' );
	}

	console.log( 'Test: %s. Elapsed time: %d sec. Iterations: %d. ops/s: %d.', name, time, N, N/time );
}

function benchmark() {
	test( 'native', Math.pow );
	test( 'pow', pow );
	test( 'fast_pow', fastpow );

	test( 'native', Math.pow );
	test( 'pow', pow );
	test( 'fast_pow', fastpow );

	test( 'native', Math.pow );
	test( 'pow', pow );
	test( 'fast_pow', fastpow );
}


// MAIN //

benchmark();
