#!/usr/bin/env julia

import JSON

"""
    gen( x, y, name )

Generate fixture data and write to file.

# Arguments

* `x`: base domain
* `y`: exponent domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> y = linspace( 0, 1000, 1001 );
julia> gen( x, y, \"data.json\" );
```
"""
function gen( x, y, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = x[i]^y[i];
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("y", y),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Random (x decimal, y integer):
x = rand( 5001 )*1000.0 - 500.0;
y = round( rand(5001) * 200.0 ) - 100;
gen( x, y, "decimal_integer.json" );

# Random (x integer, y integer):
x = round( rand(5001) * 1000.0 ) - 500;
y = round( rand(5001) * 200.0 ) - 100;
gen( x, y, "integer_integer.json" );

# Multiples of ten:
x = ones( 309 ) * 10.0;
y = linspace( 0, 308, 309 );
gen( x, y, "multiples_of_ten.json" );
