'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );
var randu = require( '@stdlib/math/base/random/randu' );
var hypot = require( '@stdlib/math/base/special/hypot' );
var fasthypot = require( '@stdlib/math/fastmath/special/hypot' );
var ampbm = require( './../lib' );


// VARIABLES //

var N = 1e7;


// FUNCTIONS //

var hypot1a = ampbm.factory( 1.0, 0.5, true );
var hypot1b = ampbm.factory( 1.0, 0.5 );
var hypot2a = ampbm.factory( 1.0, 0.25, true );
var hypot2b = ampbm.factory( 1.0, 0.25 );
var hypot3a = ampbm.factory( 1.0, 3.0/8.0, true );
var hypot3b = ampbm.factory( 1.0, 3.0/8.0 );
var hypot4a = ampbm.factory( 0.96043387010342, 0.397824734759316, true );
var hypot4b = ampbm.factory( 0.96043387010342, 0.397824734759316 );

function warmup() {
	var i;
	var j;

	j = 0;
	for ( i = 0; i < 1e6; i++ ) {
		j += i * 2;
	}
	if ( j > 0 ) {
		return true;
	}
	return false;
}

function test( name, fcn ) {
	var start;
	var time;
	var bool;
	var x;
	var y;
	var h;
	var i;

	bool = warmup();
	if ( !bool ) {
		throw new Error( 'Something went wrong.' );
	}
	start = process.hrtime();
	for ( i = 0; i < N; i++ ) {
		x = randu()*100.0 - 50.0;
		y = randu()*100.0 - 50.0;
		h = fcn( x, y );
		if ( isnan( h ) ) {
			throw new Error( 'Something went wrong.' );
		}
	}
	time = process.hrtime( start );
	time = time[ 0 ] + time[ 1 ]*1.0e-9;

	if ( isnan( h ) ) {
		throw new Error( 'Something went wrong.' );
	}
	console.log( 'Test: %s. Elapsed time: %d sec. Iterations: %d. ops/s: %d.', name, time, N, N/time );
}

function benchmark() {

	test( 'native', Math.hypot );
	test( 'hypot', hypot );
	test( 'fast_hypot', fasthypot );
	test( 'approx_hypot', ampbm );
	test( 'approx_hypot1a', hypot1a );
	test( 'approx_hypot1b', hypot1b );
	test( 'approx_hypot2a', hypot2a );
	test( 'approx_hypot2b', hypot2b );
	test( 'approx_hypot3a', hypot3a );
	test( 'approx_hypot3b', hypot3b );
	test( 'approx_hypot4a', hypot4a );
	test( 'approx_hypot4b', hypot4b );

	test( 'native', Math.hypot );
	test( 'hypot', hypot );
	test( 'fast_hypot', fasthypot );
	test( 'approx_hypot', ampbm );
	test( 'approx_hypot1a', hypot1a );
	test( 'approx_hypot1b', hypot1b );
	test( 'approx_hypot2a', hypot2a );
	test( 'approx_hypot2b', hypot2b );
	test( 'approx_hypot3a', hypot3a );
	test( 'approx_hypot3b', hypot3b );
	test( 'approx_hypot4a', hypot4a );
	test( 'approx_hypot4b', hypot4b );

	test( 'native', Math.hypot );
	test( 'hypot', hypot );
	test( 'fast_hypot', fasthypot );
	test( 'approx_hypot', ampbm );
	test( 'approx_hypot1a', hypot1a );
	test( 'approx_hypot1b', hypot1b );
	test( 'approx_hypot2a', hypot2a );
	test( 'approx_hypot2b', hypot2b );
	test( 'approx_hypot3a', hypot3a );
	test( 'approx_hypot3b', hypot3b );
	test( 'approx_hypot4a', hypot4a );
	test( 'approx_hypot4b', hypot4b );
}


// MAIN //

benchmark();
