'use strict';

// MODULES //

var tape = require( 'tape' );
var shuffle = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof shuffle, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a factory function', function test( t ) {
	t.equal( typeof shuffle.factory, 'function', 'has a factory method' );
	t.end();
});

tape( 'attached to the main export is the generator seed', function test( t ) {
	t.equal( typeof shuffle.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'attached to the main export is the underlying PRNG', function test( t ) {
	t.equal( typeof shuffle.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});
