'use strict';

var poisson = require( '@stdlib/math/base/random/poisson' );
var pmf = require( '@stdlib/math/base/dist/poisson/pmf' );
var chi2gof = require( './../lib' );

var table;
var rpois;
var probs;
var freqs;
var len;
var out;
var val;
var i;
var x;

// Draw 400 samples from a Poisson( 3.0 ) distribution:
rpois = poisson.factory( 3.0 );

len = 400;
x = new Array( len );
for ( i = 0; i < len; i++ ) {
	x[ i ] = rpois();
}

// Generate frequeny table:
freqs = [];
for ( i = 0; i < len; i++ ) {
	val = x[ i ];
	if ( freqs[ val ] === undefined ) {
		freqs[ val ] = 1;
	} else {
		freqs[ val ] += 1;
	}
}

// Fill holes in array:
for ( i = 0; i < freqs.length; i++ ) {
	if ( freqs[ i ] === undefined ) {
		freqs[ i ] = 0;
	}
}

// Get Poisson probabilities:
probs = new Array( freqs.length );
for ( i = 0; i < freqs.length; i++ ) {
	probs[ i ] = pmf( i, 3.0 );
}

out = chi2gof( freqs, probs, {
	'rescale': true
});
table = out.print();

console.log( out );
console.log( '\n' );
console.log( table );
