'use strict';

// MAIN //

/**
* Computes an element-wise comparison (greater than or equal to) of an array and a single value
*
* @param {NumericArray} arr - input array
* @param {number} x - comparator
* @returns {NumericArray} array of 1s and 0s, where a `1` indicates that an input array element is greater than or equal to a compared value and `0` indicates that an input array element is not greater than or equal to a compared value
*
* @example
* var arr = [ 1, 2, 3, 4, 5 ];
* var x = 3;
* var out = geq( arr, x );
* // returns [ 0, 0, 1, 1, 1 ]
*/
function geq( arr, x ) {
	var out;
	var len;
	var i;
	len = arr.length;
	out = new Array( len );

	for ( i = 0; i < len; i++ ) {
		if ( arr[ i ] >= x ) {
			out[ i ] = 1;
		} else {
			out[ i ] = 0;
		}
	}
	return out;
} // end FUNCTION geq()


// EXPORTS //

module.exports = geq;
