'use strict';

/**
* Sums the elements of an array.
*
* @private
* @param {Array} arr - input array
* @returns {number} array sum
*
* @example
* var arr = [ 2, 3, 5, 4 ];
* var out = sum( arr );
* // returns 14
*/
function sum( arr ) {
	var len = arr.length;
	var ret = 0;
	var i;

	for ( i = 0; i < len; i++ ) {
		ret += arr[ i ];
	}
	return ret;
} // end FUNCTION sum()


// EXPORTS //

module.exports = sum;
