'use strict';

var incrcount = require( './../lib' );

var accumulator;
var count;
var v;
var i;

// Initialize an accumulator:
accumulator = incrcount();

// For each simulated datum, update the count...
for ( i = 0; i < 100; i++ ) {
	v = Math.random() * 100.0;
	count = accumulator( v );
	console.log( 'Count: %d', count );
}
console.log( '\nFinal count: %d\n', accumulator() );
