'use strict';

/**
* Compute a mid-range incrementally.
*
* @module @stdlib/math/generics/statistics/incrmidrange
*
* @example
* var incrmidrange = require( '@stdlib/math/generics/statistics/incrmidrange' );
*
* var accumulator = incrmidrange();
*
* var midrange = accumulator();
* // returns 0.0
*
* midrange = accumulator( 3.14 );
* // returns 0.0
*
* midrange = accumulator( -5.0 );
* // returns ~-0.93
*
* midrange = accumulator( 10.1 );
* // returns 2.55
*
* midrange = accumulator();
* // returns 2.55
*/

// MODULES //

var incrmidrange = require( './incrmidrange.js' );


// EXPORTS //

module.exports = incrmidrange;
