'use strict';

var incrmin = require( './../lib' );

var accumulator;
var min;
var v;
var i;

// Initialize an accumulator:
accumulator = incrmin();

// For each simulated datum, update the min...
console.log( '\nValue\tMin\n' );
for ( i = 0; i < 100; i++ ) {
	v = Math.random() * 100.0;
	min = accumulator( v );
	console.log( '%d\t%d', v.toFixed( 4 ), min.toFixed( 4 ) );
}
console.log( '\nFinal min: %d\n', accumulator() );
