'use strict';

/**
* Compute a moving corrected sample standard deviation incrementally.
*
* @module @stdlib/math/generics/statistics/incrmstdev
*
* @example
* var incrmstdev = require( '@stdlib/math/generics/statistics/incrmstdev' );
*
* var accumulator = incrmstdev( 3 );
*
* var s = accumulator();
* // returns null
*
* s = accumulator( 2.0 );
* // returns 0.0
*
* s = accumulator( -5.0 );
* // returns ~4.95
*
* s = accumulator( 3.0 );
* // returns ~4.36
*
* s = accumulator( 5.0 );
* // returns ~5.29
*
* s = accumulator();
* // returns ~5.29
*/

// MODULES //

var incrmstdev = require( './incrmstdev.js' );


// EXPORTS //

module.exports = incrmstdev;
