'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var incrmsummary = require( './../lib' );

var accumulator;
var summary;
var v;
var i;

// Initialize an accumulator:
accumulator = incrmsummary( 5 );

// For each simulated datum, update the moving summary...
for ( i = 0; i < 100; i++ ) {
	v = randu() * 100.0;
	summary = accumulator( v );
	console.log( 'Value: %d', v );
	console.log( summary );
	console.log( '\n' );
}
console.log( accumulator() );
