'use strict';

// MODULES //

var tape = require( 'tape' );
var abs = require( '@stdlib/math/base/special/abs' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var incrskewness = require( './../lib' );


// FUNCTIONS //

function almostEqual( a, b, tol ) {
	var delta = abs( a - b );
	tol = tol * Math.max( 1.0, abs( a ), abs( b ) );
	return ( delta <= tol );
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof incrskewness, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an accumulator function', function test( t ) {
	t.equal( typeof incrskewness(), 'function', 'returns a function' );
	t.end();
});

tape( 'the accumulator function incrementally computes a corrected sample skewness', function test( t ) {
	var expected;
	var actual;
	var bool;
	var data;
	var acc;
	var tol;
	var i;

	tol = 1 * EPS;
	data = [ 3.0, 2.0, 7.0, -1.0, 9.0 ];

	// Check against the skewness function of the `e1071` R package:
	expected = [
		null,
		null,
		1.457862967321305,
		0.4366620845757488,
		0.1171875
	];

	acc = incrskewness();
	for ( i = 0; i < data.length; i++ ) {
		actual = acc( data[ i ] );
		bool = almostEqual( expected[ i ], actual, tol );
		t.ok( bool, 'returns expected incremental result' );
	}
	t.end();
});

tape( 'if not provided an input value, the accumulator function returns the current corrected sample skewness', function test( t ) {
	var expected;
	var data;
	var bool;
	var acc;
	var tol;
	var i;

	tol = EPS;
	data = [ 1.0, 4.0, 1.0, 0.0, 2.0, 12.0 ];

	acc = incrskewness();
	for ( i = 0; i < data.length; i++ ) {
		acc( data[ i ] );
	}
	expected = 1.986829609590966;
	bool = almostEqual( acc(), expected, tol );

	t.ok( bool, 'returns the current accumulated corrected sample skewness' );
	t.end();
});

tape( 'the corrected sample skewness is `null` until at least 3 datums have been provided', function test( t ) {
	var acc;
	var skewness;

	acc = incrskewness();

	skewness = acc();
	t.equal( skewness, null, 'returns null' );

	skewness = acc( 2.0 );
	t.equal( skewness, null, 'returns null' );

	skewness = acc( 2.0 );
	t.equal( skewness, null, 'returns null' );

	skewness = acc( 3.0 );
	t.notEqual( skewness, 0.0, 'does not return 0' );

	t.end();
});
