'use strict';

var incrstdev = require( './../lib' );

var accumulator;
var sigma;
var v;
var i;

// Initialize an accumulator:
accumulator = incrstdev();

// For each simulated datum, update the sample standard deviation...
console.log( '\nValue\tSigma\n' );
for ( i = 0; i < 100; i++ ) {
	v = Math.random() * 100.0;
	sigma = accumulator( v );
	console.log( '%d\t%d', v.toFixed( 4 ), sigma.toFixed( 4 ) );
}
console.log( '\nFinal standard deviation: %d\n', accumulator() );
