'use strict';

/**
* Compute a sample standard deviation incrementally.
*
* @module @stdlib/math/generics/statistics/incrstdev
*
* @example
* var incrstdev = require( '@stdlib/math/generics/statistics/incrstdev' );
*
* var accumulator = incrstdev();
*
* var s = accumulator();
* // returns 0.0
*
* s = accumulator( 2.0 );
* // returns 0.0
*
* s = accumulator( -5.0 );
* // returns ~4.95
*
* s = accumulator();
* // returns ~4.95
*/

// MODULES //

var incrstdev = require( './incrstdev.js' );


// EXPORTS //

module.exports = incrstdev;
