'use strict';

/**
* Returns an accumulator function which incrementally computes a sum.
*
* @returns {Function} accumulator function
*
* @example
* var accumulator = incrsum();
*/
function incrsum() {
	var sum = 0.0;

	/**
	* If provided a value, the accumulator function returns an updated sum. If not provided a value, the accumulator function returns the current sum.
	*
	* @param {number} [x] - new value
	* @returns {number} sum
	*
	* @example
	* var sum = accumulator();
	* // returns 0.0
	*
	* sum = accumulator( 2.0 );
	* // returns 2.0
	*
	* sum = accumulator( -5.0 );
	* // returns -3.0
	*
	* sum = accumulator();
	* // returns -3.0
	*/
	return function accumulator( x ) {
		if ( arguments.length === 0 ) {
			return sum;
		}
		sum += x;
		return sum;
	};
} // end FUNCTION incrsum()


// EXPORTS //

module.exports = incrsum;
