'use strict';

var incrsummary = require( './../lib' );

var accumulator;
var summary;
var v;
var i;

// Initialize an accumulator:
accumulator = incrsummary();

// For each simulated datum, update the summary...
for ( i = 0; i < 100; i++ ) {
	v = Math.random() * 100.0;
	summary = accumulator( v );
	console.log( 'Value: %d', v );
	console.log( summary );
	console.log( '\n' );
}
console.log( accumulator() );
