'use strict';

/**
* Compute a statistical summary incrementally.
*
* @module @stdlib/math/generics/statistics/incrsummary
*
* @example
* var incrsummary = require( '@stdlib/math/generics/statistics/incrsummary' );
*
* var accumulator = incrsummary();
*
* var summary = accumulator();
* // returns {}
*
* summary = accumulator( 2.0 );
* // returns {...}
*
* summary = accumulator( -5.0 );
* // returns {...}
*
* summary = accumulator();
* // returns {...}
*/

// MODULES //

var incrsummary = require( './incrsummary.js' );


// EXPORTS //

module.exports = incrsummary;
