'use strict';

var kstest = require( './../lib' );
var rnorm = require( '@stdlib/math/base/random/normal' );

var table;
var out;
var i;
var x;

// Values drawn from a Normal(3,1) distribution
x = new Array( 100 );
for ( i = 0; i < 100; i++ ) {
	x[ i ] = rnorm( 3.0, 1.0 );
}

// Test against N(0,1)
out = kstest( x, 'normal', 0.0, 1.0 );
table = out.toString();

console.log( 'Test against N(0,1):');
console.log( table );

// Test against N(3,1)
out = kstest( x, 'normal', 3.0, 1.0 );
table = out.toString();

console.log( 'Test against N(3,1):');
console.log( table );
