'use strict';

/**
* Compute a Kolmogorov-Smirnov goodness-of-fit test.
*
* @module @stdlib/math/generics/statistics/kstest
*
* @example
* var generator = require( '@stdlib/math/base/random/normal' ).factory;
* var kstest = require( '@stdlib/math/generics/statistics/kstest' );
*
* var rnorm;
* var out;
* var i;
* var x;
*
*
* // Values drawn from a Normal(3,1) distribution
* rnorm = generator( 3.0, 1.0, {
* 	'seed': 293
* });
* x = new Array( 100 );
* for ( i = 0; i < 100; i++ ) {
* 	x[ i ] = rnorm();
* }
*
* // Test against N(0,1)
* out = kstest( x, 'normal', 0.0, 1.0 );
* // returns { pValue: 0, T: ~0.901 },
*
* // Test against N(3,1)
* out = kstest( x, 'normal', 3.0, 1.0 );
* // returns { pValue: ~0.234, T: ~0.102 },
*/

// MODULES //

var kstest = require( './kstest.js' );


// EXPORTS //

module.exports = kstest;
