'use strict';

/**
* Computes the arithmetic mean of a numeric array.
*
* @param {NumericArray} arr - input array
* @returns {number} arithmetic mean
*
* @example
* var arr = [ 2.0, 6.0, 10.0 ];
* var out = mean( arr );
* // returns 6.0
*/
function mean( arr ) {
	var delta;
	var len;
	var mu;
	var i;

	len = arr.length;
	mu = 0.0;
	for ( i = 0; i < len; i++ ) {
		delta = arr[ i ] - mu;
		mu += delta / (i+1);
	}
	return mu;
} // end FUNCTION mean()


// EXPORTS //

module.exports = mean;
