setReadOnly( stdmath, 'PINF', require( '@stdlib/math/base/constants/float64-pinf' ) );
setReadOnly( stdmath, 'NINF', require( '@stdlib/math/base/constants/float64-ninf' ) );
setReadOnly( stdmath, 'FLOAT32_PINF', require( '@stdlib/math/base/constants/float32-pinf' ) );
