'use strict';

var OnlineSGDClassifier = require( './../lib' );
var randu = require( '@stdlib/math/base/random/randu' );
var x1;
var x2;
var y;
var i;

// Create model:
var model = new OnlineSGDClassifier({
	'lambda': 1e-4,
	'loss': 'leastSquares',
	'intercept': true
});

// Data comes in...
for ( i = 0; i < 100000; i++ ) {
	x1 = rand();
	x2 = rand();
	y = ( 3.0 * x1 + -3.0 * x2 ) > 0.0 ? +1 : -1;
	model.update( y, [ x1, x2 ] );
}

// Extract model coefficients:
console.log( model.coefs );

// Predict new observations:
console.log( 'y_hat = %d; x1 = %d; x2 = %d', model.predict( [0.9,0.1] ), 0.9, 0.1 );
console.log( 'y_hat = %d; x1 = %d; x2 = %d', model.predict( [0.1,0.9] ), 0.1, 0.9 );
console.log( 'y_hat = %d; x1 = %d; x2 = %d', model.predict( [0.9,0.9] ), 0.9, 0.9 );
