'use strict';

/**
* Update weights given new observation (y,x) when the used
* loss function is the epsilon-insensitive loss (penalty is the absolute value
* of the dot product of the weights and `x` minus `y` whenever the absolute error exceeds epsilon,
* and zero otherwise).
*
* @private
* @param {WeightVector} weights - current model coefficients
* @param {number} y - response value
* @param {NumericArray} x - feature vector
* @param {PositiveNumber} eta - current learning rate
* @param {PositiveNumber} epsilon - insensitivity parameter
*/
function epsilonInsensitiveLoss( weights, y, x, eta, epsilon ) {
	var p = weights.innerProduct( x ) - y;
	if ( p > epsilon ) {
		weights.add( x, -eta );
	} else if ( p < -epsilon ) {
		weights.add( x, +eta );
	}
} // end METHOD epsilonInsensitiveLoss()


// EXPORTS //

module.exports = epsilonInsensitiveLoss;
