'use strict';

/**
* Update weights given new observation (y,x) when the used loss function is the Huber loss,
* which uses squared-error loss for observations with error smaller than epsilon in magnitude and
* and linear loss above that in order to decrease the influence of outliers on the model fit.
*
* @private
* @param {WeightVector} weights - current model coefficients
* @param {number} y - response value
* @param {NumericArray} x - feature vector
* @param {PositiveNumber} eta - current learning rate
* @param {PositiveNumber} epsilon - insensitivity parameter
*/
function huberLoss( weights, y, x, eta, epsilon ) {
	var p = weights.innerProduct( x ) - y;
	if ( p > epsilon ) {
		weights.add( x, -eta );
	} else if ( p < -epsilon ) {
		weights.add( x, +eta );
	} else {
		weights.add( x, -eta * p );
	}
} // end FUNCTION huberLoss()


// EXPORTS //

module.exports = huberLoss;
